  interface GroupProps {
    "id": number,
    "name": string,
    "closed": boolean,
    "avatar_color"?: string,
    "members_count": number,
    "friends"?: User[]
  }
    interface User {
    "first_name": string,
    "last_name": string
  }

const GroupCard: React.FC<GroupProps> = (props) => {

  console.log(props.name);
  const color=props.avatar_color;

return (

    <Space direction="vertical" size={16}>
      <Card title={props.name} extra={<a href="#">More</a>} style={{ width: 300 }} >
      <Meta
          avatar={<Avatar style={{ backgroundColor:color, width:'150' }}/>}
          title={props.name}
          description="This is the description"
        />
         <p>{props.name}</p> 
      </Card>
    </Space>
  );
    }


export default GroupCard;
