import React, { useState, FC } from 'react';
import { useAppDispatch } from '../../hooks';
import {
  Form,
  Button
} from 'antd';

import './filterForm.css'
import AvatarFilter from './AvatarFilter';
import FriendsFilter from './FriendFilter';
import PrivacyFilter from './PrivacyFilter';
import { clearFilters } from '../../store/groupSlice';

// type SizeType = Parameters<typeof Form>[0]['size'];

const FilterForm: FC = () => {
    const dispatch=useAppDispatch();
//   const [componentSize, setComponentSize] = useState<SizeType | 'default'>('default');

//   const onFormLayoutChange = ({ size }: { size: SizeType }) => {
//     setComponentSize(size);
//   };

function showUserModal(){
    dispatch(clearFilters());
    // console.log('show')
}

  return (
    <Form
    className='aside-card'
    // labelAlign='right'
    //   labelCol={{ span: 10 }}
    //   wrapperCol={{ span: 14 }}
    //   layout="horizontal"
    layout="vertical"
    //   initialValues={{ size: 'large' }}
    //   onValuesChange={onFormLayoutChange}
    //   size={componentSize as SizeType}
    size='large'

      style={{ maxWidth: 600, border:'1px solid #f7f8fa', borderRadius:10, boxShadow:'0 0 8px #e6e6e6' , backgroundColor:'#f7f8fa'}}
    >
      <Form.Item  label="Приватность" style={{marginLeft:10}}>
<PrivacyFilter/>
      </Form.Item >
      <Form.Item label="Аватар" style={{marginLeft:10}}>
<AvatarFilter/>
      </Form.Item>
      <Form.Item label="Количество друзей" style={{marginLeft:10}}>
<FriendsFilter/>
      </Form.Item>
      <Form.Item >
          <Button htmlType="button" style={{ margin: '0 auto' }} onClick={showUserModal} block>
            Очистить фильтры
          </Button>
        </Form.Item>

    </Form>
  );
};

export default FilterForm;
