import { FC } from "react"
import { fetchGroups } from "../store/groupSlice";
import { useEffect } from 'react';
import { useAppDispatch, useAppSelector} from '../hooks'
import GroupCard from "./GroupCard";



const GroupList = () => {
    // const dispatch = useAppDispatch();
    const {groups, status, error, privacyFilter, avatarFilter, friendsFilter} = useAppSelector(state=> state.groups);
    console.log('groups',groups)
    // useEffect(()=>{
    //    groups=dispatch(fetchGroups());
    //   console.log('effect');

    //     }, [dispatch]);
    const filteredGroups = groups.filter(group => {
        if (privacyFilter!==null && group.closed !== privacyFilter) {
          return false; // Если параметр 1 не соответствует фильтру, исключаем элемент
        }
        if (avatarFilter && group.avatar_color !== avatarFilter) {
          return false; // Если параметр 2 не соответствует фильтру, исключаем элемент
        }
        if (friendsFilter && group.friends.length() > friendsFilter) {
            return false; // Если параметр 2 не соответствует фильтру, исключаем элемент
          }
        // Если элемент проходит все фильтры, оставляем его
        return true;
      });
    console.log('fultered', filteredGroups)

    return (
        <div>
        {filteredGroups.map((group) => 
            // console.log(group);
        <GroupCard  key={group.id} {...group}/>
        )}
        {/* {status + error} */}
        </div>
    )

}

export default GroupList;
