      setTimeout(() => {
        resolve({ ok: true, data: require('./groups.json') });
      }, 1000);
    });

    if (response.ok) {
        console.log(response.data)
      return response.data;
    } else {
      throw new Error('Failed to fetch groups');
    }
  });



