import { useSelector } from "react-redux";
import Filter from "./Filter"; 
// import { changePlatform } from "../store/gameSlice";
import { changeFriendsFilter } from "../store/groupSlice";

export default function FriendsFilter() {
  const friendsFilter = useSelector(state=> state.groups.friendsFilter )
  const options = [
    {
      value: null,
      label: "Все",
    },
    {
      value: 0,
      label: "нет друзей",
    },
    {
      value: 1,
      label: "есть друзья",
    },
  ];
  return (
    <Filter
      placeholder="наличию друзей"
      options={options}
      setFilter={changeFriendsFilter}
      value={friendsFilter}
    />
  );
}
