// import { useSelector } from "react-redux";
import { useAppSelector } from "../../hooks";
import Filter from "./Filter"; 
import { FC } from "react";
// import { changePlatform } from "../store/gameSlice";
import { changeFriendsFilter } from "../../store/groupSlice";

 const FriendsFilter:FC=()=> {
  const friendsFilter = useAppSelector(state=> state.groups.friendsFilter )
  const options = [
    {
      value: null,
      label: "Любое",
    },
    {
      value: false,
      label: "нет друзей",
    },
    {
      value: true,
      label: "есть друзья",
    },
  ];
  return (
    <Filter
      placeholder="наличию друзей"
      options={options}
      setFilter={changeFriendsFilter}
      value={friendsFilter}
    />
  );
}

export default FriendsFilter;
