import { FC } from "react"
// import { fetchGroups } from "../store/groupSlice";
// import { useEffect } from 'react';
import { useAppSelector} from '../hooks'
import GroupCard from "./GroupCard";



const GroupList = () => {
    const {groups, status, error, privacyFilter, avatarFilter, friendsFilter} = useAppSelector(state=> state.groups);
    console.log('groups',groups)
    const filteredGroups = groups.filter(group => {
        if (privacyFilter!==null && group.closed !== privacyFilter) {
          return false;
        }
        if (avatarFilter && group.avatar_color !== avatarFilter) {
          return false; 
        }
        if (friendsFilter!==null){
            if (group.friends && friendsFilter===0){
                return false
            }
            else if (!group.friends && friendsFilter===1){
                return false
            }
        } 

        return true;
      });

    return (
        <div>
                  {status==='loading' && <Loader/>}
      <div>{status}</div>
        {filteredGroups.map((group) => 
        <GroupCard  key={group.id} {...group}/>
        )}
        </div>
    )

}

export default GroupList;
