import {
  Form,
} from 'antd';
import './filterForm.css'
import AvatarFilter from './AvatarFilter';
import FriendsFilter from './FriendFilter';
import PrivacyFilter from './PrivacyFilter';

// type SizeType = Parameters<typeof Form>[0]['size'];

const FilterForm: FC = () => {
//   const [componentSize, setComponentSize] = useState<SizeType | 'default'>('default');

//   const onFormLayoutChange = ({ size }: { size: SizeType }) => {
//     setComponentSize(size);
//   };

  return (
    <Form
    className='aside-card'
    labelAlign='right'
    //   labelCol={{ span: 10 }}
    //   wrapperCol={{ span: 14 }}
    //   layout="horizontal"
    layout="vertical"
    //   initialValues={{ size: 'large' }}
    //   onValuesChange={onFormLayoutChange}
    //   size={componentSize as SizeType}
    size='large'

      style={{ maxWidth: 600, border:'1px solid black', borderRadius:10, boxShadow:'1px black' }}
    >
      <Form.Item  label="Приватность" style={{marginLeft:10}}>
<PrivacyFilter/>
      </Form.Item>
      <Form.Item label="Аватар">
<AvatarFilter/>
      </Form.Item>
      <Form.Item label="Количество друзей">
<FriendsFilter/>
      </Form.Item>


    </Form>
  );
};

export default FilterForm;
