import { useSelector } from "react-redux";
import Filter from "./Filter"; 
import { changeAvatarFilter } from "../store/groupSlice";

export default function AvatarFilter() {
  const avatarFilter = useSelector(state=> state.groups.avatarFilter )
  const options = [
    {
      value: null,
      label: "Все",
    },
    {
      value: 'red',
      label: "красный",
    },
    {
      value: 1,
      label: "есть друзья",
    },
  ];
  return (
    <Filter
      placeholder="наличию друзей"
      options={options}
      setFilter={changeAvatarFilter}
      value={avatarFilter}
    />
  );
}
