import { FC } from "react";
import { Select } from "antd";
import { useAppDispatch } from "../hooks";

interface Options{
value:string|boolean|null, 
label:string
}

interface FilterProps{
placeholder:string, 
options:Options[], 
value:string|boolean|null,
// setFilter:(value:string|boolean|null)=>void,
// setFilter: ActionCreatorWithPayload<boolean | null>
setFilter:any,
}

const Filter:FC<FilterProps>=({placeholder, options, value, setFilter})=> {
const dispatch=useAppDispatch();
  const onChange = (value:string|boolean|null) => {
    dispatch(setFilter(value))
  };
  return (
    <Select
      showSearch
      placeholder={`Фильтровать по ${placeholder}`}
      style={{ width: "53%" }}
      onChange={onChange}
      filterOption={(input, option) =>
        (option?.label ?? "").toLowerCase().includes(input.toLowerCase())
      }
      options={options}
      value={value}
    />
  );
}
export default  Filter;
